<script>

import { Menu, MenuButton, MenuItems, MenuItem } from '@headlessui/vue'

import { RouterLink, RouterView } from "vue-router";
import Facebook from "/src/components/icones/facebook.vue";
import Instagram from "/src/components/icones/instagram.vue";
import Youtube from "/src/components/icones/youtube.vue";
import Bouton from "/src/components/icones/bouton.vue"
import MenuIcon from "./components/icones/menu.vue";
import Profil from "./components/icones/profil.vue";
import croix from "/src/components/icones/croix.vue";
import {  } from "@heroicons/vue/outline";



// Fonctions Firestore
import { 
    getFirestore, 
    collection, 
    onSnapshot, 
    query,
    where
} from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-firestore.js'


// Fonctions Storage
import { 
    getStorage, 
    ref, 
    getDownloadURL, 
} from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-storage.js'

// Fonction authentification
import { getAuth } from 'https://www.gstatic.com/firebasejs/9.7.0/firebase-auth.js'

// Import emetteur de main.js
import { emitter } from './main.js'

export default {
  components: { 
    RouterLink,
    RouterView,
    Facebook, 
    Instagram, 
    Youtube,
    Bouton,
    MenuIcon,
    Profil,
    croix
  },
  
  data() {
    return {
      menuOuvert:false,
      user:{              // User connectÃ©
        email:null,
        password:null
      },
      userInfo:null,      // Informations complÃ©mentaires user connectÃ©
      name:"VidÃ©o",       // Titre de l'application ou nom du user
      avatar:null,        // Avatar / image du user connectÃ©
      isAdmin:false       // Si l'utilisateur est ou non administrateur
    }
  },
  mounted(){
    // VÃ©rifier si un user connectÃ© existe dÃ©jÃ 
    // Au lancement de l'application
    this.getUserConnect();

    // Ecoute de l'Ã©vÃ¨nement de connexion
    emitter.on('connectUser', e => {
      // RÃ©cupÃ©ration du user
      this.user = e.user;
//      console.log('App => Reception user connectÃ©', this.user);
      // Recherche infos complÃ©mentaires du user
      this.getUserInfo(this.user);      
    })        
    
    // Ecoute de l'Ã©vÃ¨nement de deconnexion
    emitter.on('deConnectUser', e => {
      // RÃ©cupÃ©ration user 
      this.user = e.user;
//      console.log('App => Reception user deconnectÃ©', this.user);
      // RÃ©initialisation infos complÃ©mentaires user
      this.userInfo   = null;
      this.name       = 'VidÃ©o';
      this.avatar     = null;
      this.isAdmin    = false;
    })
  },
  methods:{
    // Obtenir les informations du user connectÃ©
    async getUserConnect(){
      await getAuth().onAuthStateChanged(function(user) { 
        if(user) {
            // RÃ©cupÃ©ration du user connectÃ©
            this.user = user;
            // Recherche de ses infos complÃ©mentaires
            this.getUserInfo(this.user);   
        } 
      }.bind(this));
      // Noter le bind(this), cas des zones isolÃ©es
      // lors de 2 strucutres imbiquÃ©es, Vue perd la visibilitÃ©
      // des donnÃ©es
      // On les rÃ© injecte par le mot-clef this
    },

    async getUserInfo(user){
      // Rechercher les informations complÃ©mentaires de l'utilisateur
      // Obtenir Firestore
      const firestore = getFirestore();
      // Base de donnÃ©es (collection)  document participant
      const dbUsers = collection(firestore, "users");
      // Recherche du user par son uid
      const q = query(dbUsers, where("uid", "==", user.uid));
      await onSnapshot(q, (snapshot) => {
          this.userInfo = snapshot.docs.map(doc => (
            {id:doc.id, ...doc.data()}
          ));
//          console.log("userInfo", this.userInfo);                
          // userInfo Ã©tant un tableau, onn rÃ©cupÃ¨re
          // ses informations dans la 1Â° cellule du tableau : 0
          this.name = this.userInfo[0].login;
          this.isAdmin=this.userInfo[0].admin;
          // Recherche de l'image du user sur le Storage
          const storage = getStorage();
          // RÃ©fÃ©rence du fichier avec son nom
          const spaceRef = ref(storage, 'users/'+this.userInfo[0].avatar);
          getDownloadURL(spaceRef)
            .then((url) => {
              this.avatar = url;
            })
            .catch((error) =>{
              console.log('erreur downloadUrl', error);
            })
        });
      }
    } 
}




</script>